import React from 'react';
import logo from './logo.svg';
import './App.css';
import Autocomplete from 'react-autocomplete'
import { Row, Col, FormGroup, Label, Input , UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem , Button} from "reactstrap";

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      avatar : "https://github.com/images/error/octocat_happy.gif",
      location : "",
      email : "",
      bio : "",
      profileLink : "",
      totalRepos : "",
      followers : "",
      userName : "josh",
      usersList : []
    }

    this.getUserProfile = this.getUserProfile.bind(this);
    this.getUsersList = this.getUsersList.bind(this);
  }

  getUserProfile() {
    let user = this.state.userName;
    let url = "https://api.github.com/user?"+user;
    // console.log("<https://api.github.com/users?since=135>; rel='next'");
    fetch(url)
    .then(results => {
      console.log("result",results.json());
    })
    //https://api.github.com/users?since=135
    this.setState({location : "Delhi"});
  }

  getUsersList() {
    fetch("https://api.github.com/users?since=135")
    .then(results => {
      console.log("result",results.json());
    })
    this.setState({usersList : [{name :"hii", id : "hii"},{name:"hello", id:"hello"},{name:"yeah",id:"yeah"}]});
  }

  render() {
    return (
      <div className="App">
        <header className = "App-header">
          <Row className = "Header-row">
            <Col>
              <Label>Username : </Label>
              <Autocomplete value = {this.state.username}
                            items = {this.state.usersList}
                            onChange = {(event, value) => {
                              this.getUsersList(event, value)
                            }}
                            getItemValue={(item) => item.name}
                            renderItem={(item, isHighlighted) => (
                      		            <div
                      		              className = "App-autoComplete"
                                        key={item.id}>{item.name}</div>
                      		          )}
                            >
              </Autocomplete>
              <Button className = "App-button" onClick = {this.getUserProfile}>Submit</Button>
              <div style = {{'textAlign' : 'right'}}>
                <img src={this.state.avatar} className="App-logo" alt="logo" />
              </div>
            </Col>
          </Row>
        </header>

        <FormGroup className = "App-body">
          <Row>
            <Col>
              <h2>User Profile</h2>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Location : </Label>
              <Label>{this.state.location}</Label>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Email : </Label>
              <Label>{this.state.email}</Label>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Bio : </Label>
              <Label>{this.state.bio}</Label>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Profile link : </Label>
              <Label>{this.state.profileLink}</Label>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Total Number of Starred Repo's : </Label>
              <Label>{this.state.totalRepos}</Label>
            </Col>
          </Row>

          <Row className = "App-row">
            <Col>
              <Label>Followers : </Label>
              <Label>{this.state.followers}</Label>
            </Col>
          </Row>

        </FormGroup>
      </div>
    );
  }
}

export default App;
